document.addEventListener("DOMContentLoaded",function(){const f=document.getElementById("registrationForm");if(f)f.addEventListener("submit",function(e){e.preventDefault();let u=document.getElementById("username").value.trim(),m=document.getElementById("email").value.trim(),p=document.getElementById("password").value.trim(),msg="";if(!u)msg="O nome de usuário é obrigatório.";else if(!m||!/^[^@]+@[^@]+\.[^@]+$/.test(m))msg="Email inválido.";else if(!p||p.length<6)msg="A senha deve ter pelo menos 6 caracteres.";if(msg){alert(msg);return}let user={username:u,email:m};let users=JSON.parse(localStorage.getItem("users")||"[]");users.push(user);localStorage.setItem("users",JSON.stringify(users));alert("Usuário registrado com sucesso!");f.reset()})});